mapper.select:
SELECT * 
FROM dandy_member 
WHERE mid=#{mid} AND mpw=#{mpw}

SELECT *
FROM question_board
WHERE bno = #{bno}

SELECT *
FROM question_reply
WHERE bno = #{bno}

mapper.update:
UPDATE question_board
	SET viewcnt = viewcnt+1
	WHERE bno = #{bno}

UPDATE question_board
SET re_step = re_step + 1
WHERE ref=#{ref} AND re_step >= #{re_step}

mapper.select:
SELECT *
FROM question_board
ORDER BY ${keyword} ${sort}

mapper.update:
UPDATE board
SET title = #{title}, content = #{content}
WHERE bno = #{bno}

UPDATE board
	SET viewcnt = viewcnt+1
	WHERE bno = #{bno}

UPDATE board
	SET goodcnt = goodcnt+1
	WHERE bno = #{bno}

mapper.select:
SELECT filename
FROM board
WHERE bno= #{bno}

mapper.update:
UPDATE board
	SET downcnt = downcnt+1
	WHERE bno = #{bno}

UPDATE board
SET re_step = re_step + 1
WHERE ref = #{ref} AND re_step >= #{re_step}

mapper.select:
SELECT *
FROM fbreply
WHERE bno = #{bno}


	SELECT *
	FROM (
		SELECT rownum rnum,
		a.*
		FROM (
			SELECT * 
			FROM movieindex 
			
				
					WHERE mno > 0 
				
				
					WHERE mno > 0 AND indexchar = #{index}
				
				
			
			ORDER BY mno
		) a
	)
	WHERE rnum BETWEEN #{pageStart} AND #{perPageNum}

SELECT count(mno) FROM movieindex
WHERE mno > 0
WHERE mno > 0 AND indexchar = #{index}

SELECT *
FROM dandy_member
WHERE mid=#{mid}

	select title, img, mno
	FROM MOVIEINDEX
	WHERE mno = (select mno${i}
    		    from dandy_member
        		where mid=#{mid}
    )
