mapper.select:
SELECT * 
FROM dandy_member 
WHERE mid=#{mid} AND mpw=#{mpw}

mapper.update:
UPDATE question_board
	SET viewcnt = viewcnt+1
	WHERE bno = #{bno}

UPDATE question_board
SET re_step = re_step + 1
WHERE ref=#{ref} AND re_step >= #{re_step}

UPDATE board
SET title = #{title}, content = #{content}
WHERE bno = #{bno}

UPDATE board
	SET viewcnt = viewcnt+1
	WHERE bno = #{bno}

UPDATE board
	SET goodcnt = goodcnt+1
	WHERE bno = #{bno}

mapper.select:
SELECT filename
FROM board
WHERE bno= #{bno}

mapper.update:
UPDATE board
	SET downcnt = downcnt+1
	WHERE bno = #{bno}

UPDATE board
SET re_step = re_step + 1
WHERE ref = #{ref} AND re_step >= #{re_step}

mapper.select:

	SELECT *
	FROM (
		SELECT rownum rnum,
		a.*
		FROM (
			SELECT * 
			FROM movieindex 
			
				WHERE mno > 0 AND indexchar = #{index}
			
			
				WHERE mno > 0 
			
			
			ORDER BY mno
		) a
	)
	WHERE rnum BETWEEN #{pageStart} AND #{perPageNum}


	SELECT *
	FROM (
		SELECT rownum rnum,
		a.*
		FROM (
			SELECT * 
			FROM movieindex 
			
				WHERE mno > 0 AND indexchar = #{index}
			
			
				WHERE mno > 0 
			
			
			ORDER BY mno
		) a
	)
	WHERE rnum BETWEEN #{pageStart} AND #{perPageNum}

SELECT count(mno) FROM movieindex
WHERE mno > 0 AND indexchar = #{index}
WHERE mno > 0
