mapper.update:
UPDATE board
SET title = #{title}, content = #{content}
WHERE bno = #{bno}

UPDATE board
	SET viewcnt = viewcnt+1
	WHERE bno = #{bno}

UPDATE board
	SET goodcnt = goodcnt+1
	WHERE bno = #{bno}

mapper.select:
SELECT filename
FROM board
WHERE bno= #{bno}

mapper.update:
UPDATE board
	SET downcnt = downcnt+1
	WHERE bno = #{bno}

UPDATE board
SET re_step = re_step + 1
WHERE ref = #{ref} AND re_step >= #{re_step}
